import React, {useEffect, useRef, useState} from 'react';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, {tableCellClasses} from '@mui/material/TableCell';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import {styled} from "@mui/material/styles";
import DropTable from "./dropTable";

const StyledTableRow = styled(TableRow)(({ theme }) => ({
    '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
    },
    // hide last border
    '&:last-child td, &:last-child th': {
        border: 0,
    },
}));
const StyledTableCell = styled(TableCell)(({ theme }) => ({
    [`&.${tableCellClasses.head}`]: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
    },
    [`&.${tableCellClasses.body}`]: {
        fontSize: 14,
    },
}));

const DropdownMenu = () => {
    const dropdownRef = useRef(null);
    const [isActive, setIsActive] = useState(false);
    const onClick = () => setIsActive(!isActive);

    return onClick,isActive;
};

function withMyHook(Component) {
    return function WrappedComponent(props) {
        const myHookValue = DropdownMenu();
        return <Component {...props} myHookValue={myHookValue} />;
    }
}

class DropTableRow extends React.Component {
    render() {

        const myHookValue = this.props.myHookValue;
        console.log(myHookValue)

        return (
            <React.Fragment>
                <StyledTableRow key={this.props.rest.name}>
                    <StyledTableCell>
                        <IconButton
                            aria-label="expand row"
                            size="small"
                            onClick={() => !myHookValue}
                        >
                            {myHookValue ? <KeyboardArrowUpIcon/> : <KeyboardArrowDownIcon/>}
                        </IconButton>
                    </StyledTableCell>
                    <StyledTableCell component="th" scope="row">
                        {this.props.rest.name}
                    </StyledTableCell>
                    <StyledTableCell align="right">{this.props.rest.location}</StyledTableCell>
                    <StyledTableCell align="right">{this.props.rest.max_capacity}</StyledTableCell>
                </StyledTableRow>
                <StyledTableRow>
                    <StyledTableCell style={{paddingBottom: 0, paddingTop: 0}} colSpan={6}>
                        <Collapse in={myHookValue} timeout="auto" unmountOnExit>
                            <Box sx={{margin: 1}}>
                                <Typography variant="h6" gutterBottom component="div">
                                    Menu
                                </Typography>
                                <Table size="small" aria-label="purchases">
                                    <TableHead>
                                        <StyledTableRow>
                                            <StyledTableCell>Name</StyledTableCell>
                                            <StyledTableCell align="right">Total price ($)</StyledTableCell>
                                        </StyledTableRow>
                                    </TableHead>
                                    <TableBody>
                                        <StyledTableRow key={this.props.rest.name}>
                                            <StyledTableCell component="th" scope="row">
                                                {this.props.rest.name}
                                            </StyledTableCell>
                                            <StyledTableCell>{this.props.rest.location}</StyledTableCell>
                                            <StyledTableCell
                                                align="right">{this.props.rest.max_capacity}</StyledTableCell>
                                        </StyledTableRow>
                                    </TableBody>
                                </Table>
                            </Box>
                        </Collapse>
                    </StyledTableCell>
                </StyledTableRow>
            </React.Fragment>
        )
    }
}
export default withMyHook(DropTableRow);
